import React from 'react';
import { NominatedMovieInfo } from '../styles'

const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);

useEffect(() => {
	fetch(`http://www.omdbapi.com/?i=${id}&apikey=15d60055`)
		.then(res => res.json())
		.then(
			(result) => {
				setIsLoaded(true);
				setItems(result);
			},
			// Note: it's important to handle errors here
			// instead of a catch() block so that we don't swallow
			// exceptions from actual bugs in components.
			(error) => {
				setIsLoaded(true);
				setError(error);
			}
		)
}, [])

const MovieList = (props) => {
	const FavouriteComponent = props.favouriteComponent;
	const url = `http://www.omdbapi.com/?i=tt9775896&apikey=15d60055`
	return (
		<>
			{props.movies.map((movie, movieData, index) => (
				<div className='d-flex'>
					<h1>{}</h1>
					{movie.imdbID}
					<div className='image-container d-flex justify-content-start m-3'>
						<img src={movie.Poster} alt='movie' width="100px"></img>
						<div
							onClick={() => props.handleFavouritesClick(movie)}
							className='overlay d-flex align-items-center justify-content-center'
						>
							<FavouriteComponent />
						</div>
					</div>
					<NominatedMovieInfo>{movieData.Title}</NominatedMovieInfo>
				</div>
			))}
		</>
	);
};

export default MovieList;
