import React from 'react';
import { NominatedMovieInfo } from '../styles'



const MovieList = (props) => {
	const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);

  const onChange = (e) => {
    e.preventDefault();

    setQuery(e.target.value);

    fetch(
      `https://api.themoviedb.org/3/search/movie?api_key=${process.env.REACT_APP_TMDB_KEY}&language=en-US&page=1&include_adult=false&query=${e.target.value}`
    )
      .then((res) => res.json())
      .then((data) => {
        if (!data.errors) {
          setResults(data.results);
        } else {
          setResults([]);
        }
      });
  };

	const FavouriteComponent = props.favouriteComponent;
	const url = `http://www.omdbapi.com/?i=tt9775896&apikey=15d60055`
	return (
		<>
			{props.movies.map((movie, movieData, index) => (
				<div className='d-flex'>
					<h1>{}</h1>
					{movie.imdbID}
					<div className='image-container d-flex justify-content-start m-3'>
						<img src={movie.Poster} alt='movie' width="100px"></img>
						<div
							onClick={() => props.handleFavouritesClick(movie)}
							className='overlay d-flex align-items-center justify-content-center'
						>
							<FavouriteComponent />
						</div>
					</div>
					<NominatedMovieInfo>{movieData.Title}</NominatedMovieInfo>
				</div>
			))}
		</>
	);
};

export default MovieList;
