import React from 'react';
import { NominatedMovieInfo } from '../styles'


export const queryOmdbNomination = (movieTitle, movieYear) => {

	return dispatch => {
			dispatch(nominationQueryStarted());

			// OMDB Movie API
			const omdbUrl = `https://www.omdbapi.com/?t=${movieTitle}&y=${movieYear}&apikey=${process.env.REACT_APP_OMDB_KEY}`;

			axios.get(omdbUrl)
					.then((res) => {
							const response = res.data;
							dispatch(nominationQuerySucceeded(response));

					})
					.catch((error) => {
							dispatch(nominationQueryFailed(error));
					})
	}

}

const MovieList = (props) => {
	const FavouriteComponent = props.favouriteComponent;
	return (
		<>
			{props.movies.map((movie, movieData, index) => (
				<div className='d-flex'>
					<h1>{}</h1>
					{movie.imdbID}
					<div className='image-container d-flex justify-content-start m-3'>
						<img src={movie.Poster} alt='movie' width="100px"></img>
						<div
							onClick={() => props.handleFavouritesClick(movie)}
							className='overlay d-flex align-items-center justify-content-center'
						>
							<FavouriteComponent />
						</div>
					</div>
					<NominatedMovieInfo>{movieData.Title}</NominatedMovieInfo>
				</div>
			))}
		</>
	);
};

export default MovieList;
