import React from 'react';
import { NominatedMovieInfo } from '../styles'
<script src="https://unpkg.com/axios/dist/axios.min.js"></script>

function getMovies(searchText){
  axios.get('http://www.omdbapi.com?s='+searchText)
    .then((response) => {
      console.log(response);
      let movies = response.data.Search;
      let output = '';
      $.each(movies, (index, movie) => {
        output += `
          <div class="col-md-3">
            <div class="well text-center">
              <img src="${movie.Poster}">
              <h5>${movie.Title}</h5>
              <a onclick="movieSelected('${movie.imdbID}')" class="btn btn-primary" href="#">Movie Details</a>
            </div>
          </div>
        `;
      });

      $('#movies').html(output);
    })
    .catch((err) => {
      console.log(err);
    });
}

const getMovieData = (id) => {
	const url = `http://www.omdbapi.com/?i=${id}&apikey=15d60055`
}
const MovieList = (props) => {
	const FavouriteComponent = props.favouriteComponent;
	const url = `http://www.omdbapi.com/?i=tt9775896&apikey=15d60055`
	return (
		<>
			{props.movies.map((movie, movieData, index) => (
				<div className='d-flex'>
					<h1>{}</h1>
					{movie.imdbID}
					<div className='image-container d-flex justify-content-start m-3'>
						<img src={movie.Poster} alt='movie' width="100px"></img>
						<div
							onClick={() => props.handleFavouritesClick(movie)}
							className='overlay d-flex align-items-center justify-content-center'
						>
							<FavouriteComponent />
						</div>
					</div>
					<NominatedMovieInfo>{movieData.Title}</NominatedMovieInfo>
				</div>
			))}
		</>
	);
};

export default MovieList;
